import { useState, useEffect } from 'react';
import Link from 'next/link';
import Router from 'next/router';
import { isLoggedIn, getCookie } from '../actions/handleCookie';
import { createCategory,listCategory,deleteCategory } from '../actions/category';

const Category = () => {
    const [values, setValues] = useState({
        name: '',
        error: false,
        success: false,
        categories: [],
        removed: false
    });

    const { name, error, success, categories, removed } = values;
    const token = getCookie('token');


    useEffect(()=>{ //call when state is changed 
    	loadCategories()
    },[])

    //load all categories when the page is mounted(lifecycle)
    const loadCategories = () => {
    	listCategory().then(categories=>{
    		if(categories.error){
    			console.log(categories.error)
    		} else {
    			setValues({...values,categories: categories})
    		}
    	})
    }

    const showCategories = () => {
    	return categories.map((category,index)=>{
    		  <button key={index} className="btn btn-outline-primary mr-1 ml-1 mt-3">
                    {category.name}
               </button>
    	})
    }

/*onDoubleClick={() => deleteConfirm(c.slug)}
                    title="Double click to delete"*/

    const handleSubmit = event => {
        event.preventDefault();
        createCategory({ name }, token).then(category => {
            if (category.error) {
                setValues({ ...values, error: category.error, success: false });
            } else {
                setValues({  ...values, error: false, success: false, name: ''});
            }
        });
    };

    const handleChange = event => {
        setValues({ ...values, name: event.target.value, error: false, success: false, removed: '' });
    };

    const newCategoryFom = () => (
        <form onSubmit={handleSubmit}>
            <div className="form-group">
                <label className="text-muted">Name</label>
                <input type="text" className="form-control" onChange={handleChange} value={name} required />
            </div>
            <div>
                <button type="submit" className="btn btn-primary">
                    Create
                </button>
            </div>
        </form>
    );

    return <React.Fragment>
    		{newCategoryFom()}
    		<div>
    			{showCategories()}
    		</div>
    	</React.Fragment>;
};

export default Category;
