import getConfig from 'next/config'
const {publicRuntimeConfig} = getConfig();

export const API = publicRuntimeConfig.PRODUCTION ? '' : 'http://localhost:8000/api';
export const APP_NAME = publicRuntimeConfig.APP_NAME;
export const DOMAIN = publicRuntimeConfig.PRODUCTION ? publicRuntimeConfig.PRODUCTION : publicRuntimeConfig.DOMAIN_DEVELOPMENT;


//accessing environment variable and export them
