const RegisterComponent = () => {
	const handleSubmit = (event) => {
		event.preventDefault()
		console.log('handleSubmit')
	}

	const handleChange = (event) => {
		event.preventDefault()
		console.log(event.target.value)
	}
	//event value will be taken to the state 

	const registerForm = () => {
	return(
		<form onSubmit={handleSubmit}>
		<div className="form-group">
			<input onChange={handleChange} type="text" className="formControl" placeholder="Your name" /> 
		</div>
		<div className="form-group">
			<input onChange={handleChange} type="email" className="formControl" placeholder="Your email" /> 
		</div>
		<div className="form-group">
			<input onChange={handleChange} type="password" className="formControl" placeholder="Your password" /> 
		</div>
		<div>
			<button className="btn btn-primary">Register</button>
		</div>
		</form>
	)
	}

	return (
		<div>
		{registerForm()}
		</div>
	)
}

export default RegisterComponent
