import {useState} from 'react'
import {listSearch} from '../actions/journal'

const Search = () => {
	const [values,setValues] = useState({
		search: undefined,
		result: [],
		searched: false,
		message: ""
	})

	const handleChange = event => {
		setValues({...values,search: event.target.value,searched: false,result: []});
	}

	const handleSubmit = event => {
		event.preventDefault()
		listSearch({search}).then(data=>{
			setValues({...values,result: data,searched: true,message: `${data.length} journals founded`})
		})
	}

	const showSearchBar = () => {
		return <form onSubmit={handleSubmit}>
			<div className="row">
				<div className="col-md-10">
					<input 
						type="search" 
						className="form-control" 
						placeholder="Search journal" 
						onChange = {handleChange} />
				</div>
				<div className="col-md-2">
					<button className="btn btn-block btn-outline-primary">Search</button>
				</div>
			</div>
		</form>
	}

	return <div className="container-fluid">
		<div className="pt-2 pb-2">
			{showSearchBar()}
		</div>
	</div>
}

export default Search
