import {useState} from 'react'
import {listSearch} from '../actions/journal'
import queryString from 'query-string'

const Search = () => {

	const [values,setValues] = useState({
		search: undefined,
		result: [],
		searched: false,
		message: ""
	})

	const {search,result,searched,message} = values

	const handleSubmit = event => {
		listSearch({search}).then(data=>{
			setValues({...values,search: event.target.value,result: data, searched: true, message: `${data.length} journals found` })
		})
	}


	const handleChange = event => {
		setValues({...values,search: event.target.value,searched: false,resutl: []})
	}

	const showSearchBar = () => {
		return <form onSubmit={handleSubmit}>
			<div className="row">
				<div className="col-md-8">
					<input type="search" className="form-control" placeholder="Search for journals here" onChange={handleChange}/>
				</div>

				<div className="col-md-4">
					<button className="btn btn-block btn-outline-primary" type="submit">Search</button>
				</div>

			</div>
		</form>
	}	

	return (
		<div className="container-fluid">
			<div className="pt-3 pb-5">
				{showSearchBar()}
			</div>
		</div>
	)

}

export default Search
