import { useState, useEffect } from 'react';
import Link from 'next/link';
import Router from 'next/router';
import { isLoggedIn, getCookie } from '../actions/handleCookie';
import { createCategory } from '../actions/category';

const Category = () => {
    const [values, setValues] = useState({
        name: '',
        error: false,
        success: false,
        categories: [],
        removed: false
    });

    const { name, error, success, categories, removed } = values;
    const token = getCookie('token');

    const handleSubmit = e => {
        e.preventDefault();
        console.log('create category', name);
        /*create({ name }, token).then(data => {
            if (data.error) {
                setValues({ ...values, error: data.error, success: false });
            } else {
                setValues({ ...values, error: false, success: true, name: '' });
            }
        });*/
    };

    const handleChange = e => {
        setValues({ ...values, name: e.target.value, error: false, success: false, removed: '' });
    };

    const newCategoryFom = () => (
        <form onSubmit={handleSubmit}>
            <div className="form-group">
                <label className="text-muted">Name</label>
                <input type="text" className="form-control" onChange={handleChange} value={name} required />
            </div>
            <div>
                <button type="submit" className="btn btn-primary">
                    Create
                </button>
            </div>
        </form>
    );

    return <React.Fragment>{newCategoryFom()}</React.Fragment>;
};

export default Category;
