import {useState} from 'react'
import Layout from '../../components/Layout';
import {forgotPassword} from '../../actions/auth'

const ForgotPassword = () => {
	const [values,setValues] = useState({
		email: '',
		message: '',
		error: '',
		isSent: false
	})

	const {email,message,error,isSent} = values

	const handleChange =  e => {
		setValues({...values,email:e.target.value,error:''})
		console.log(email)
	}

	const handleSubmit = e => {
		e.preventDefault()
		forgotPassword({email}).then((data)=>{
			if(data.error){
				setValues({...values,error:data.error})
			} else {
				setValues({...values,email:'',error:'',isSent: true,message:data.message})
			}
		})

	}

	const showForm = () => {
			if(!isSent){
				return (
					<form onSubmit={handleSubmit}>
					<div className="row">
					<div className="form-group">
					<input
						onChange={handleChange}
						value={email}
						type="email" 
						className="form-control"
						placeholder="Please enter your email"
						required
					/>
				</div>
				<div>
					<button className="btn btn-primary">Submit</button>
				</div>

			</div>
			</form>
				)
			}
	}

	const showStatus = () => {
		if(error){
			return <div className="alert alert-danger">{error}</div>
		} else if(isSent) {
			return <div className="alert alert-success">{message}</div>
		}
	}

	return (
		<Layout>
			<div className="container-fluid">
				<h3 className="col-md-4 offset-md-5 mt-3 mb-3">Forgot Password</h3>
				<div className="col-md-2 offset-md-5 mt-3 mb-3">
					{showStatus()}
					{showForm()}
				</div>
				
			</div>
		</Layout>
	)
}

export default ForgotPassword
