import {listSearch} from '../actions/journal'
import {useState} from 'react'

const Search = () => {
	const [values,setValues] = useState({
		search: undefined,
		results: [],
		searched: false,
		message: ''
	})

	const {search,results,searched,message} = values

	const searchSubmit = event => {
		event.preventDefault()
	}

	const handleChange = event => {
		console.log(event)
	}

	const showSearchBar = () => {
		return <form onSubmit={searchSubmit}>
		<div className="row">
			<div className="col-md-8">
				<input type="search" className="fomr-control" placeholder="Search for journals" onchange={handleChange}/>
			</div>

			<div className="col-md-4">
				<button className="btn btn-lock btn-outline-primary" type="submit">Search</button>
			</div>
		</div>

		</form> 
	}

	return (
		<div className="container-fluid">
		<div className="pt-3 pb-5">
			{showSearchBar()}
		</div>
		</div>
	)
}

export default Search
