		error:"",
		success:""
	}]

	const {name,email,message,error,success} = values

	const handleSubmit = () => {
		event.preventDefault()
		console.log("submit")
	}

	const handleChange = event => {
		console.log(event.target)
	}

	const displayForm = () => {
		return (
			<form onSubmit={handleSubmit}>
				<div className="form-group pt-2 pb-2">
					<input onChange={handleChange}  className="form-control" type="text" placeholder="Your name" />
				</div>
				<div className="form-group pt-2 pb-2">
					<input className="form-control" type="email" placeholder="Your email" />
				</div>
				<div className="form-group pt-2 pb-2">
					<textarea className="form-control" type="text" placeholder="Write your message and feedbacks here"/>
				</div>
				<button className="btn btn-primary offset-md-4">Submit</button>
			</form>
		)
	}

	return (
		<div>{displayForm()}</div>
	)
}

export default About
