import Link from 'next/link'
import {useState,useEffect} from 'react'
import Router from 'next/router'
import {getCookie,isLoggedIn} from '../../actions/handleCookie'
import {getProfile,updateProfile} from '../../actions/member'


const ProfileUpdate = () => {
	const [values,setValues] = useState({
		username: '',
		name: '',
		email:'',
		password:'',
		error: false,
		success: false,
		loading: false,
		photo:'',
		about:'',
		formData: '' 
	})	

	const token = getCookie('token')
	const {username,name,email,password,error,success,loading,photo,formData} = values

	const loadMemberProfile = () => {
		getProfile(token).then(data=>{
			console.log(data)
			if(data.error)  {
				setValues({...values,error: data.error})
			} else {
				setValues({...values,username: data.username,name: data.name,email: data.email,about: data.about, formData: new FormData() })
			}
		})
	}

	useEffect(()=>{
		loadMemberProfile()
	},[])

	//currying function
	const handleSubmit = event => {
		event.preventDefault()
	}

	const handleChange = name => {
		console.log()
	}

	const showProfileForm = () => {
		return <form onSubmit={handleSubmit}>
			<div className="form-group">
				<label className="text-muted">Profile Photo</label>
				<input onChange={handleChange('photo')} type="file" accept="image/*" className="form-control"/>
			</div>
			<div className="form-group">
				<label className="text-muted">Username</label>
				<input onChange={handleChange('username')} type="text" className="form-control"/>
			</div>
			<div className="form-group">
				<label className="text-muted">Name</label>
				<input onChange={handleChange('name')} type="text"  className="form-control"/>
			</div>
			<div className="form-group">
				<label className="text-muted">About</label>
				<input onChange={handleChange('about')} type="text" className="form-control"/>
			</div>
			<div className="form-group">
				<label className="text-muted">Password</label>
				<input onChange={handleChange('password')} type="text" className="form-control"/>
			</div>
			<button  className="btn btn-primary">Submit</button>

		</form>
	}

	return (
		<React.Fragment>
			<div className="container">
				<div className="row">
					<div className="col-md-4">
						image
					</div>
					<div className="col-md-8">
						{showProfileForm()}
					</div>
				</div>
			</div>
		</React.Fragment>
	)

}


export default ProfileUpdate

//make req to backend to get member info
