import Link from 'next/link';
import { useState, useEffect } from 'react';
import Router from 'next/router';
import { withRouter } from 'next/router'; //use router,export component with router

import { getCookie, isLoggedIn } from '../../actions/handleCookie';
import { listCategory } from '../../actions/category';
import { listTag } from '../../actions/tag';
import { createJournal } from '../../actions/journal';

import dynamic from 'next/dynamic';

//this modules will be imported dynamically
const ReactQuill = dynamic(() => import('react-quill'), { ssr: false }); //making sure that react quill only runs in frontend
/*import '../../node_modules/react-quill/dist/quill.snow.css';*/


const CreateJournal = ({ router }) => {

  //grabbing journal from local storage and set as default
    const grabJournalInLS = () => {
    	if(typeof window === 'undefined'){
    		return false;
    	}

    	if(localStorage.getItem('journal')){
    		return JSON.parse(localStorage.getItem('journal'))
    	} else {
    		return false
    	}
    }

    const [categories, setCategories] = useState([]);
    const [tags, setTags] = useState([]);

    const [checkedCategory, setCheckedCategory] = useState([]); // categories
    const [checkedTag, setCheckedTag] = useState([]); // tags

	const [body, setBody] = useState(grabJournalInLS());
    const [values, setValues] = useState({
        error: '',
        sizeError: '',
        success: '',
        formData: '',
        title: '',
        hidePublishButton: false
    });

     const { error, sizeError, success, formData, title, hidePublishButton } = values;

      useEffect(() => {
        setValues({ ...values, formData: new FormData() });
        initCategories()
        initTags()
    }, [router]); //when router change,setValue is called

       const initCategories = () => {
        listCategory().then(data => {
            if (data.error) {
                setValues({ ...values, error: data.error });
            } else {
                setCategories(data);
            }
        });
    };

       const initTags = () => {
        listTag().then(data => {
            if (data.error) {
                setValues({ ...values, error: data.error });
            } else {
                setTags(data);
            }
        });
    };


     const publishJournal = event => {
        event.preventDefault();
        console.log('ready to publishJournal');
    };

     const handleChange = name => event => {
        const value = name === 'photo' ? event.target.files[0] : event.target.value;
        formData.set(name, value);
        setValues({ ...values, [name]: value, formData, error: '' });
    };

     const handleBodyContent = event => {
        setBody(event); //push event to the response body
        formData.set('body', event);	//populated form data
        //save content to local storage
        if (typeof window !== 'undefined') {
            localStorage.setItem('journal', JSON.stringify(event));
        }
    };

    	//fix re-render issues
      const handleCheckCategory = category => () => {
        setValues({ ...values, error: '' });
        // return the first index or -1
        const clickedCategory = checkedCategory.indexOf(category);
        const all = [...checkedCategory];

        if (clickedCategory === -1) {
            all.push(category);
        } else {
            all.splice(clickedCategory, 1);
        }
        console.log(all);
        setCheckedCategory(all); //update state
        formData.set('categories', all);
    };

     const handleCheckTag = tag => () => {
        setValues({ ...values, error: '' });
        // return the first index or -1
        const clickedTag = checkedTag.indexOf(tag);
        const all = [...checkedTag];

        if (clickedTag === -1) {
            all.push(tag);
        } else {
            all.splice(clickedTag, 1);
        }
        console.log(all);
        setCheckedCategory(all); //update state
        formData.set('tags', all);
    };

    const showCategories = () => {
        return (
            categories && categories.map((category, index) => (
                <li key={index} className="list-unstyled">
                    <input onChange={handleCheckCategory(category._id)} type="checkbox" className="mr-2" />
                    <label className="form-check-label">{category.name}</label>
                </li>
            ))
        );
    }

        const showTags = () => {
        return (
            tags && tags.map((tag,index) => (
                <li key={index} className="list-unstyled">
                    <input onChange={handleCheck(tag._id)} type="checkbox" className="mr-2" />
                    <label className="form-check-label">#{tag.name}</label>
                </li>
            ))
        );
    }

 const createJournalForm = () => {
        return (
            <form onSubmit={publishJournal}>
                <div className="form-group">
                    <label>Title</label>
                    <input type="text" className="form-control" value={title} onChange={handleChange('title')} />
                </div>

                <div className="form-group">
                    <ReactQuill modules={CreateJournal.modules} formats={CreateJournal.formats} value={body} placeholder="Write something..." onChange={handleBodyContent} />
                </div>

                <div>
                    <button type="submit" className="btn btn-primary">
                        Publish
                    </button>
                </div>
            </form>
        );
    };

    return <div className="container-fluid">
    	<div className="row">
    		<div className="col-md-8">
    		{createJournalForm()}
    	<hr/>
    	{JSON.stringify(title)}
    	<hr/>
    	{JSON.stringify(body)}
    	<hr/>
    	{JSON.stringify(categories)}
    	<hr/>
    	{JSON.stringify(tags)}
    		</div>
    		<div className="col-md-4">
    		<h4>Categories</h4>
    		<hr/>
    		<ul style={{maxHeight: '100px',overflowY: 'scroll'}}>{showCategories()}</ul>
    		<h4>Tags</h4>
    		<hr/>
    		<ul style={{maxHeight: '100px',overflowY: 'scroll'}}>{showTags()}</ul>
    		</div>
    	</div>

    </div>;
};

CreateJournal.modules = {
    toolbar: [
        [{ header: [1,2,3, 4, 5, 6] }, { font: [] }],
        [{ size: [] }],
        ['bold', 'italic', 'underline', 'strike'],
        [{ list: 'ordered' }, { list: 'bullet' }],
        ['link', 'image', 'video'],
        ['clean'],
        ['code-block']
    ]
};
 
CreateJournal.formats = [
    'header',
    'font',
    'size',
    'bold',
    'italic',
    'underline',
    'strike',    
    'list',
    'bullet',
    'link',
    'image',
    'video',
    'code-block'
];

export default withRouter(CreateJournal);
