import Link from 'next/link';
import { useState, useEffect } from 'react';
import Router from 'next/router';
import { withRouter } from 'next/router'; //use router,export component with router
import { getCookie, isLoggedIn } from '../../actions/handleCookie';
import { listCategory } from '../../actions/category';
import { listTag } from '../../actions/tag';
import { createJournal,readJournal } from '../../actions/journal';
import dynamic from 'next/dynamic';


//Quill does not suppport SSR, so it's only loaded and rendered in the browser.
const ReactQuill = dynamic(() => import('react-quill'), { ssr: false }); 

const AmendJournal = ({router}) => {

    const [journal,setJournal] = useState({})

    useEffect(()=>{
        loadJournal()
    },[])

    const loadJournal = () => {
        if(router.query.slug){
            readJournal(router.query.slug).then(data=>{
                console.log(data)
                if(data.error){
                    console.log(data.error)
                } else {
                    setJournal(data)
                }
            })
        }
    }


    return <div className="container-fluid">
                <div className="row">

                    <div className="col-md-8">
                        <p>Update Status</p>
                    </div>

                    <div className="col-md-8">
                        {JSON.stringify(journal)}
                    </div>

                    <div className="col-md-4">
                        <div className="form-group pb-2">
                            <h5>Featured Image</h5>
                            <hr/>
                            <small className="text-muted">Maximum Size: 1MB</small>
                            <label className="btn btn-outline-info">Change Featured Image</label>
                        </div>
                        <h4>Categories</h4>
                        <hr/>
                        <h4>Tags</h4>
                        <hr/>
                    </div>
             </div>
        </div>
}

AmendJournal.modules = {
    toolbar: [
        [{ header: [1,2,3,4,5,6] }, { font: [] }],
        [{ size: [] }],
        ['bold', 'italic', 'underline', 'strike'],
        [{ list: 'ordered' }, { list: 'bullet' }],
        ['link', 'image', 'video'],
        ['clean'],
        ['code-block']
    ]
};
 
AmendJournal.formats = [
    'header',
    'font',
    'size',
    'bold',
    'italic',
    'underline',
    'strike',    
    'list',
    'bullet',
    'link',
    'image',
    'video',
    'code-block'
];

export default withRouter(AmendJournal)
